mapper.select:
        SELECT
		chat_record_id,
		content,
		create_date,
		update_date,
		account_id,
		to_account_id
	FROM chat_record_tb


			AND account_id=#{accountId}


			AND to_account_id=#{toAccountId}

		AND ${orderName}

		>=


		  <= 

		(SELECT ${orderName} FROM chat_record_tb


					AND account_id=#{accountId}


					AND to_account_id=#{toAccountId}


			ORDER BY
				${orderName}  ${orderWay}
			LIMIT #{pageNum}, 1
		)

	ORDER BY
		${orderName}  ${orderWay}
	LIMIT
		#{pageSize}

SELECT
 	count(to_account_id)
FROM chat_record_tb


		AND account_id=#{accountId}


		AND to_account_id=#{toAccountId}
