mapper.select:
      SELECT
		circle_id,
		content,
		praise_points,
		create_date,
		update_date,
		account_id
	FROM circle_tb


			AND account_id=#{accountId}

		AND ${orderName}

		>=


		  <= 

		(SELECT ${orderName} FROM circle_tb


					AND account_id=#{accountId}


			ORDER BY
				${orderName}  ${orderWay}
			LIMIT #{pageNum}, 1
		)

	ORDER BY
		${orderName}  ${orderWay}
	LIMIT
		#{pageSize}
