mapper.select:
      SELECT
		chat_room_id,
		type,
		name,
		url,
		create_date,
		update_date,
		account_id
	FROM chat_room_tb


			AND account_id=#{accountId}

		AND ${orderName}

		>=


		  <= 

		(SELECT ${orderName} FROM chat_room_tb


					AND account_id=#{accountId}


			ORDER BY
				${orderName}  ${orderWay}
			LIMIT #{pageNum}, 1
		)

	ORDER BY
		${orderName}  ${orderWay}
	LIMIT
		#{pageSize}
