mapper.select:
      SELECT
		chat_room_record_id,
		content,
		create_date,
		update_date,
		chat_room_id,
		account_id
	FROM chat_room_record_tb


			AND chat_room_id=#{chatRoomId}


			AND account_id=#{accountId}

		AND ${orderName}

		>=


		  <= 

		(SELECT ${orderName} FROM chat_room_record_tb


					AND chat_room_id=#{chatRoomId}


					AND account_id=#{accountId}


			ORDER BY
				${orderName}  ${orderWay}
			LIMIT #{pageNum}, 1
		)

	ORDER BY
		${orderName}  ${orderWay}
	LIMIT
		#{pageSize}

SELECT
 	count(chat_room_record_id)
FROM chat_room_record_tb


		AND chat_room_id=#{chatRoomId}


		AND account_id=#{accountId}
